'use strict';

// =================================================================================
// App Configuration
// =================================================================================

const app = require('../../index').Jovo;
const webhook = require('../../index').Webhook;

app.enableRequestLogging();
app.enableResponseLogging();
const SILENCE_SOUND = 'https://s3.amazonaws.com/viki-ticker/silence.mp3';
const SILENCE_SOUND_TOKEN = 'silence';

const sounds = [
    'https://www.swetlow.de/uneasy.mp3',
];


// Listen for post requests
webhook.listen(3000, function() {
    console.log('Local development server listening on port 3000.');
});

webhook.post('/webhook', function(req, res) {
    app.handleRequest(req, res, handlers);
    app.execute();
});


// =================================================================================
// App Logic
// =================================================================================

const handlers = {

    'LAUNCH': function() {
        app.toIntent('StartIntent');
    },

    'StartIntent': function() {
        app.alexaSkill().audioPlayer().startOver(SILENCE_SOUND, SILENCE_SOUND_TOKEN).tell('Gestartet');
    },
    'AMAZON.PauseIntent': function() {
        app.alexaSkill().audioPlayer().stop().endSession();
    },
    'AUDIOPLAYER': {
        'AudioPlayer.PlaybackNearlyFinished': function () {
            console.log('AudioPlayer.PlaybackNearlyFinished');
            playFile();
        },
    },
};

function playFile() {
    // let randomnumber = Math.floor(Math.random() * (100 - 1 + 1)) + 1;
    // if(randomnumber%2 === 0) {
    app.alexaSkill().audioPlayer().startOver(sounds[0], sounds[0]);
    // } else {
    //     audioPlayer.startOver(SILENCE_SOUND, SILENCE_SOUND_TOKEN)
    // }
}